import React from "react";
import ScrollToBottom from "react-scroll-to-bottom";

const Messages = ({ messages, name }) => {
  return (
    <div>
      <ScrollToBottom>
        {messages.map((message, i) => (
          <div key={i}>
            <Message message={message} />
          </div>
        ))}
      </ScrollToBottom>
    </div>
  );
};

export default Messages;
